function CountLeftTime(){var e=null,n=null,t=null,o=null,i=new Date,m=new Date("2016/09/30"),l=m.getTime()-i.getTime();e=Math.floor(l/864e5),n=Math.floor(l/36e5%24),t=Math.floor(l/6e4%60),o=Math.floor(l/1e3%60),document.getElementById("DayTime").innerHTML=e,document.getElementById("HourTime").innerHTML=n,document.getElementById("MinutesTime").innerHTML=t,document.getElementById("SecondsTime").innerHTML=o,setTimeout(CountLeftTime,1e3)}window.onload=function(){CountLeftTime()};